/**
 * Get hexagram by ID
 */
export function getHexagramById(id: number): HexagramContent | undefined {
  return hexagramContents.find(h => h.id === id);
}

/**
 * Get all hexagrams
 */
export function getAllHexagrams(): HexagramContent[] {
  return hexagramContents;
}
